import axios from 'axios';

import { GET_PROFILES, PROFILE_ERROR, CLEAR_PROFILE } from './types';

// Get all current profile
export const getProfiles = () => async dispatch => {
  dispatch({ type: CLEAR_PROFILE });

  try {
    const res = await axios.get('/api/owner/all');

    dispatch({
      type: GET_PROFILES,
      payload: res.data
    });
  } catch (err) {
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.response.statustext, status: err.response.status }
    });
  }
};

// Get all profile by id
export const getProfileByID = userID => async dispatch => {
  try {
    const res = await axios.get(`/api/getOwner/${userID}`);

    dispatch({
      type: GET_PROFILE,
      payload: res.data
    });
  } catch (err) {
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.response.statustext, status: err.response.status }
    });
  }
};
