import axios from 'axios';
import { setAlert } from '../Sub-Admin/alert';

import {
  GET_PROFILES,
  PROFILE_ERROR,
  CLEAR_PROFILE,
  GET_PROFILE,
  UPDATE_PROFILE,
  SET_ALERT
} from './types';

// Get all current profile
export const getProfiles = () => async dispatch => {
  dispatch({ type: CLEAR_PROFILE });

  try {
    const res = await axios.get('/api/subAdmin/viewVehicle');

    dispatch({
      type: GET_PROFILES,
      payload: res.data
    });
  } catch (err) {
    dispatch({
      type: PROFILE_ERROR
      //payload: { msg: err.response.statustext, status: err.response.status }
    });
  }
};

// Get all profile by id
export const getProfileById = userID => async dispatch => {
  try {
    const res = await axios.get(`/api/subAdmin/viewVehicleById/${userID}`);

    dispatch({
      type: GET_PROFILE,
      payload: res.data
    });
  } catch (err) {
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.response.statustext, status: err.response.status }
    });
  }
};

//Delete Vehicle
export const deleteVehicleBySubAdmin = id => async dispatch => {
  //if (window.confirm('Are You Sure? This will permanantly delete the Vehicle'))
  try {
    const res = await axios.delete(`/api/owner/subAdmin/delete/${id}`);

    dispatch({
      type: UPDATE_PROFILE
    });

    dispatch(setAlert('Vehicle Deleted', 'success'));
  } catch (err) {
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.response.statustext, status: err.response.status }
    });
  }
};
