import {
  GET_PROFILES,
  PROFILE_ERROR,
  GET_PROFILE,
  CLEAR_PROFILE
} from '../actions/types';

const initialState = {
  profile: null,
  profiles: [],
  vehicle: [],
  repos: [],
  loading: true,
  error: null
};

export default function(state = initialState, action) {
  const { type, payload, payload2 } = action;

  switch (type) {
    case GET_PROFILE:
      return {
        ...state,
        profile: payload,
        loading: false
      };

    case GET_PROFILES:
      return {
        ...state,
        profiles: payload,
        vehicle: payload2,
        loading: false,
        error: null
      };
    case PROFILE_ERROR:
      return {
        ...state,
        error: payload,
        loading: false
      };
    case CLEAR_PROFILE:
      return {
        ...state,
        profile: null,
        repos: [],
        loading: false
      };
    default:
      return state;
  }
}
