import React, { Fragment, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const SubAdmin = () => {
  const [subAdmins, setSubAdmins] = useState([]);

  useEffect(() => {
    async function loadSubAdmins() {
      try {
        const res = await axios.get('/api/admin/subAdmin');
        setSubAdmins(res.data);
      } catch (err) {
        console.log(err.message);
      }
    }
    loadSubAdmins();
  }, []);

  const deleteSubAdmin = async id => {
    try {
      await axios.delete(`/api/admin/delete/subadmin/${id}`);
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <Fragment>
      <div className='container'>
        <div className='row'>
          <div className='col-12 heading'>Sub Admins</div>
        </div>
        <div className='row'>
          <div className='col-12'>
            <Link className='btn btn-primary' to='/subAdmin/add'>
              Add Sub Admin
            </Link>
          </div>
        </div>
        <div className='row'>
          <div className='col-12'>
            <table className='table table-striped'>
              <thead>
                <tr>
                  <th scope='col'>Name</th>
                  <th scope='col'>City</th>
                  <th scope='col'>Actions</th>
                </tr>
              </thead>
              <tbody>
                {subAdmins.map(subAdmin => (
                  <tr>
                    <td>{subAdmin.name}</td>
                    <td>{subAdmin.city}</td>
                    <td>
                      <Link
                        className='btn btn-primary'
                        style={{ marginRight: '0.5rem' }}
                        to={`/subAdmin/add/${subAdmin._id}`}
                      >
                        View Details
                      </Link>
                      <button
                        className='btn btn-danger'
                        onClick={() => deleteSubAdmin(subAdmin._id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default SubAdmin;
