import React, { Fragment, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getProfileById } from '../../actions/OwnerProfile';
import Spinner from '../layout/spinner';
import { Link } from 'react-router-dom';

const OwnerById = ({
  getProfileById,
  profile: { profile, loading },
  auth,
  match
}) => {
  useEffect(() => {
    getProfileById(match.params.id);
  }, [getProfileById, match.params.id]);

  return (
    <Fragment>
      {profile === null || loading ? (
        <Spinner />
      ) : (
        <Fragment>
          <Link to='/owners' className='btn btn-light'>
            Back To Owners
          </Link>

          <div class='profile-grid my-1'>
            <div class='profile-top bg-primary p-2'>
              <img
                class='round-img my-1'
                src='https://www.gravatar.com/avatar/205e460b479e2e5b48aec07710c08d50?s=200'
                alt=''
              />
              <h1 class='large'>John Doe</h1>
              <p class='lead'>Developer at Microsoft</p>
              <p>Seattle, WA</p>
              <div class='icons my-1'>
                <Link href='#' target='_blank' rel='noopener noreferrer'>
                  <i class='fas fa-globe fa-2x'></i>
                </Link>
                <Link href='#' target='_blank' rel='noopener noreferrer'>
                  <i class='fab fa-twitter fa-2x'></i>
                </Link>
                <a href='#' target='_blank' rel='noopener noreferrer'>
                  <i class='fab fa-facebook fa-2x'></i>
                </a>
                <a href='#' target='_blank' rel='noopener noreferrer'>
                  <i class='fab fa-linkedin fa-2x'></i>
                </a>
                <a href='#' target='_blank' rel='noopener noreferrer'>
                  <i class='fab fa-youtube fa-2x'></i>
                </a>
                <a href='#' target='_blank' rel='noopener noreferrer'>
                  <i class='fab fa-instagram fa-2x'></i>
                </a>
              </div>
            </div>
          </div>
        </Fragment>
      )}
    </Fragment>
  );
};
OwnerById.propTypes = {
  getProfileById: PropTypes.func.isRequired,
  profile: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  profile: state.profile,
  auth: state.auth
});

export default connect(mapStateToProps, { getProfileById })(OwnerById);
