import React, { Fragment, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getProfiles } from '../../actions/OwnerProfile';
import OwnerItem from './OwnerItem';

const Owners = ({
  getProfiles,
  profile: { profiles, loading },
  auth: { isAuthenticated }
}) => {
  useEffect(() => {
    getProfiles();
  }, [getProfiles]);

  const authLinks = (
    <Fragment>
      {loading ? (
        'spinner'
      ) : (
        <Fragment>
          <h1 className='large text-primary'>Owners</h1>
          <p className='lead'>
            <i className='fab fa-connectdevelop'></i>
            Browse and connect with Owners
          </p>
          <div className='profiles'>
            {profiles.length > 0 ? (
              profiles.map(profile => (
                <OwnerItem key={profile._id} profile={profile} />
              ))
            ) : (
              <h4> Browsing...... </h4>
            )}
          </div>
        </Fragment>
      )}
    </Fragment>
  );

  const guestLink = <Fragment></Fragment>;

  return (
    <nav className='navbar bg-dark'>
      {!loading && (
        <Fragment>{isAuthenticated ? authLinks : guestLink}</Fragment>
      )}
    </nav>
  );
};

Owners.propTypes = {
  getProfiles: PropTypes.func.isRequired,
  profile: PropTypes.object.isRequired
};

const mapStateToProp = state => ({
  profile: state.profile
});

export default connect(mapStateToProp, { getProfiles })(Owners);
