import React, { Fragment, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getProfileById } from '../../actions/OwnerProfile';
import Spinner from '../layout/spinner';
import { Link } from 'react-router-dom';
import ProfileTop from './ProfileTop';

const OwnerById = ({
  getProfileById,
  profile: { profile, loading },
  auth,
  match
}) => {
  useEffect(() => {
    getProfileById(match.params.id);
  }, [getProfileById, match.params.id]);

  return (
    <Fragment>
      {profile === null || loading ? (
        <Spinner />
      ) : (
        <Fragment>
          <Link to='/owners' className='btn btn-light'>
            Back To Owners
          </Link>
          <div class='profile-grid my-1'>
            <div class='profile-top bg-primary p-2'>
              <img class='round-img my-1' src={download} alt='' />
              <h1 class='large'> {name} </h1>
              <p class='lead'>Work as Owner at SAWARI</p>
              <p>Office Base {address}</p>
              <div class='icons my-1'>
                <i class='fas fa-car fa-2x'></i>
                {'   '}
                <i class='fas fa-motorcycle fa-2x'></i>
              </div>
            </div>
          </div>
        </Fragment>
      )}
    </Fragment>
  );
};
OwnerById.propTypes = {
  getProfileById: PropTypes.func.isRequired,
  profile: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  profile: state.profile,
  auth: state.auth
});

export default connect(mapStateToProps, { getProfileById })(OwnerById);
