import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  USER_LOADED,
  AUTH_ERROR,
  LOGIN_FAIL,
  LOGIN_SUCCESS,
  LOGOUT,
  USER_IS_ADMIN,
  USER_IS_SUBADMIN
} from '../actions/Sub-Admin/types';

const initialState = {
  token: localStorage.getItem('token'),
  isAuthenticated: null,
  loading: true,
  user: null,
  userType: -1
};

export default function(state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
    case USER_IS_ADMIN:
      localStorage.setItem('userType', 0);
      return {
        ...state,
        loading: false,
        userType: 0
      };
    case USER_IS_SUBADMIN:
      localStorage.setItem('userType', 1);
      return {
        ...state,
        loading: false,
        userType: 1
      };
    case USER_LOADED:
      return {
        ...state,
        isAuthenticated: true,
        loading: false,
        user: payload
      };
    case REGISTER_SUCCESS:
    case LOGIN_SUCCESS:
      localStorage.setItem('token', payload.token);
      return {
        ...state,
        ...payload,
        isAuthenticated: true,
        loading: false
      };

    case AUTH_ERROR:
    case REGISTER_FAIL:
    case LOGIN_FAIL:
    case LOGOUT:
      localStorage.removeItem('token');
      localStorage.removeItem('userType');
      return {
        ...state,
        isAuthenticated: false,
        loading: false
      };
    default:
      return state;
  }
}
